mapper.select:
select u.user_id, u.username, u.phone, o.* from (select * from tb_order
status= '%${bo.status}%'
) o join (select user_id, username, phone from tb_user where phone = '%${bo.val}%' or username like '%${bo.val}%') u on u.user_id = o.user_id order by o.order_date desc

select b.book_id, b.book_name, b.book_cover, o.* from (select * from tb_order
status= '%${bo.status}%'
) o join (select book_id,book_name,book_cover from tb_book where book_name like '%${bo.val}%') b b.book_id=o.book_id order by o.order_date desc
